import { PackageAmex } from './package-amex';
import { PackageAmexFilter } from './package-amex-filter';
import { Injectable } from '@angular/core';
import { EMPTY, Observable } from 'rxjs';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';

const headers = new HttpHeaders().set('Accept', 'application/json');

@Injectable()
export class PackageAmexService {
  packageAmexList: PackageAmex[] = [];
  api = 'https://c7df-130-250-229-20.ngrok.io/api/package';

  constructor(private http: HttpClient) {
  }

  findById(id: string): Observable<PackageAmex> {
    const url = `${this.api}/${id}`;
    const params = { id: id };
    return this.http.get<PackageAmex>(url, {params, headers});
  }

  load(filter: PackageAmexFilter): void {
    this.find(filter).subscribe({
      next: result => {
        this.packageAmexList = result;
      },
      error: err => {
        console.error('error loading', err);
      }
    });
  }

  find(filter: PackageAmexFilter): Observable<PackageAmex[]> {
    const params = {
      'customer': filter.customer,
    };

    return this.http.get<PackageAmex[]>(this.api, {params, headers});
  }

  save(entity: PackageAmex): Observable<PackageAmex> {
    let params = new HttpParams();
    let url = '';
    if (entity.id) {
      url = `${this.api}/${entity.id.toString()}`;
      params = new HttpParams().set('ID', entity.id.toString());
      return this.http.put<PackageAmex>(url, entity, {headers, params});
    } else {
      url = `${this.api}`;
      return this.http.post<PackageAmex>(url, entity, {headers, params});
    }
  }

  delete(entity: PackageAmex): Observable<PackageAmex> {
    let params = new HttpParams();
    let url = '';
    if (entity.id) {
      url = `${this.api}/${entity.id.toString()}`;
      params = new HttpParams().set('ID', entity.id.toString());
      return this.http.delete<PackageAmex>(url, {headers, params});
    }
    return EMPTY;
  }
}
