import { Package } from './package';
import { PackageFilter } from './package-filter';
import { Injectable } from '@angular/core';
import { EMPTY, Observable } from 'rxjs';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';

const headers = new HttpHeaders().set('Accept', 'application/json');

@Injectable()
export class PackageService {
  packageList: Package[] = [];
  api = 'https://c7df-130-250-229-20.ngrok.io/api/package';

  constructor(private http: HttpClient) {
  }

  findById(id: string): Observable<Package> {
    const url = `${this.api}/${id}`;
    const params = { id: id };
    return this.http.get<Package>(url, {params, headers});
  }

  load(filter: PackageFilter): void {
    this.find(filter).subscribe({
      next: result => {
        this.packageList = result;
      },
      error: err => {
        console.error('error loading', err);
      }
    });
  }

  find(filter: PackageFilter): Observable<Package[]> {
    const params = {
      'customer': filter.customer,
    };

    return this.http.get<Package[]>(this.api, {params, headers});
  }

  save(entity: Package): Observable<Package> {
    let params = new HttpParams();
    let url = '';
    if (entity.id) {
      url = `${this.api}/${entity.id.toString()}`;
      params = new HttpParams().set('ID', entity.id.toString());
      return this.http.put<Package>(url, entity, {headers, params});
    } else {
      url = `${this.api}`;
      return this.http.post<Package>(url, entity, {headers, params});
    }
  }

  delete(entity: Package): Observable<Package> {
    let params = new HttpParams();
    let url = '';
    if (entity.id) {
      url = `${this.api}/${entity.id.toString()}`;
      params = new HttpParams().set('ID', entity.id.toString());
      return this.http.delete<Package>(url, {headers, params});
    }
    return EMPTY;
  }
}
